// App.js
import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';

const changeBgColor = keyframes`
  0% {
    background: linear-gradient(to bottom, #ffffe0, #ffffff);
  }
  100% {
    background: linear-gradient(to bottom, #ffffff, #ffffe0);
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const FloatingEmojis = styled.span`
  animation: float 3s infinite;
`;

const float = keyframes`
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0);
  }
`;

const AppContainer = styled.div`
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
  background: linear-gradient(to bottom, #ffffe0, #ffffff);
`;

const Header = styled.header`
  text-align: center;
  padding: 10px 0;
  color: #333;
  animation: ${fadeIn} 2s;
`;

const HeaderContent = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

const HeaderTitle = styled.h1`
  font-size: 2em;
  margin-right: 5px;
`;

const HeaderText = styled.p`
  font-size: 1em;
  opacity: 0;
  margin-right: 5px;
`;

const Nav = styled.nav`
  background: rgba(0, 0, 0, 0.5);
  color: #fff;
  text-align: center;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const NavItem = styled.a`
  color: #fff;
  text-decoration: none;
  padding: 8px 15px;
  transition: background-color 0.3s;
`;

const LeftPane = styled.div`
  background: rgba(0, 0, 0, 0.5);
  color: #fff;
  width: 20%;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  transition: width 0.5s;
  overflow: hidden;
`;

const Wallet = styled.div`
  color: #fff;
`;

const Footer = styled.footer`
  background: rgba(0, 0, 0, 0.5);
  text-align: center;
  padding: 10px 0;
  position: absolute;
  bottom: 0;
  width: 100%;
  color: #fff;
`;

const RightAligned = styled.div`
  margin-left: auto;
`;

function App() {
  const [isSidePaneOpen, setSidePaneOpen] = useState(false);

  const handleToggleSidePane = () => {
    setSidePaneOpen(!isSidePaneOpen);
  };

  return (
    <AppContainer>
      <Header>
        <HeaderContent>
          <HeaderTitle>Kitaab Ghar</HeaderTitle>
          <HeaderText>Explore & Learn</HeaderText>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '0s' }}>üìö</FloatingEmojis>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '1s' }}>‚úèÔ∏è</FloatingEmojis>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '2s' }}>üéì</FloatingEmojis>
        </HeaderContent>
      </Header>

      <Nav>
        <NavItem href="#">Home</NavItem>
        <NavItem href="#">Video Library</NavItem>
        <NavItem href="#">PDF Library</NavItem>
        <RightAligned>
          <Wallet>üí∞ 100</Wallet>
        </RightAligned>
      </Nav>

      {/* Left Pane */}
      <LeftPane style={{ width: isSidePaneOpen ? '20%' : '0' }}>
        {/* Content for Left Pane */}
      </LeftPane>

      {/* Button to toggle the side pane */}
      <button onClick={handleToggleSidePane}>Toggle Side Pane</button>

      {/* Video options can be added here */}

      <Footer>
        <p>¬© 2023 Kitaab Ghar - Your Education Video Course Website</p>
      </Footer>
    </AppContainer>
  );
}

export default App;
