// App.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

const changeBgColor = keyframes`
  0% {
    background: linear-gradient(to bottom, #ffffe0, #ffffff);
  }
  100% {
    background: linear-gradient(to bottom, #ffffff, #ffffe0);
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const FloatingEmojis = styled.span`
  animation: float 3s infinite;
`;

const float = keyframes`
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0);
  }
`;

const AppContainer = styled.div`
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  margin: 0;
  padding: 0;
  background: linear-gradient(to bottom, #ffffe0, #ffffff);
`;

const Header = styled.header`
  text-align: center;
  padding: 20px 0;
  color: #333;
  animation: ${fadeIn} 2s;
`;

const Nav = styled.nav`
  background: rgba(0, 0, 0, 0.5);
  color: #fff;
  text-align: center;
  padding: 10px 0;
  position: relative;
`;

const NavItem = styled.a`
  color: #fff;
  text-decoration: none;
  padding: 8px 15px;
  transition: background-color 0.3s;
`;

const Footer = styled.footer`
  background: rgba(0, 0, 0, 0.5);
  text-align: center;
  padding: 10px 0;
  position: absolute;
  bottom: 0;
  width: 100%;
  color: #fff;
`;

function App() {
  return (
    <AppContainer>
      <Header>
        <h1 style={{ fontSize: '2em', display: 'inline-block' }}>Kitaab Ghar</h1>
        <p style={{ fontSize: '1em', opacity: 0, display: 'inline-block' }}>Explore & Learn</p>
        <FloatingEmojis style={{ fontSize: '40px', display: 'inline-block', animationDelay: '0s' }}>üìö</FloatingEmojis>
        <FloatingEmojis style={{ fontSize: '40px', display: 'inline-block', animationDelay: '1s' }}>‚úèÔ∏è</FloatingEmojis>
        <FloatingEmojis style={{ fontSize: '40px', display: 'inline-block', animationDelay: '2s' }}>üéì</FloatingEmojis>
      </Header>

      <Nav>
        <NavItem href="#">Home</NavItem>
        <NavItem href="#">Courses</NavItem>
        <NavItem href="#">Languages</NavItem>
        <NavItem href="#">Topics</NavItem>
        <NavItem href="#">Contact</NavItem>
      </Nav>

      {/* Video options can be added here */}

      <Footer>
        <p>¬© 2023 Kitaab Ghar - Your Education Video Course Website</p>
      </Footer>
    </AppContainer>
  );
}

export default App;
