// App.js
import React, { useState, useRef, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// ... (previous code)

const SidePaneContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 20px;
`;

const SidePaneItem = styled.div`
  color: #fff;
  padding: 10px 0;
  cursor: pointer;
`;

// ... (previous code)

function App() {
  const [isSidePaneOpen, setSidePaneOpen] = useState(false);

  const handleToggleSidePane = () => {
    setSidePaneOpen(!isSidePaneOpen);
  };

  const sidePaneRef = useRef();

  useEffect(() => {
    function handleClickOutside(event) {
      if (sidePaneRef.current && !sidePaneRef.current.contains(event.target)) {
        setSidePaneOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [sidePaneRef]);

  return (
    <AppContainer>
      <Header>
        <HeaderContent>
          <HeaderTitle>Kitaab Ghar</HeaderTitle>
          <HeaderText>Explore & Learn</HeaderText>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '0s' }}>üìö</FloatingEmojis>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '1s' }}>‚úèÔ∏è</FloatingEmojis>
          <FloatingEmojis style={{ fontSize: '40px', animationDelay: '2s' }}>üéì</FloatingEmojis>
        </HeaderContent>
      </Header>

      <Nav>
        <div onClick={handleToggleSidePane} style={{ cursor: 'pointer', padding: '8px' }}>
          <span>&#9776;</span>
        </div>
        <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <NavItem href="#">Home</NavItem>
          <NavItem href="#">Video Library</NavItem>
          <NavItem href="#">PDF Library</NavItem>
        </div>
        <RightAligned>
          <Wallet>100 üí∞</Wallet>
        </RightAligned>
      </Nav>

      {isSidePaneOpen && (
        <>
          <Overlay onClick={handleToggleSidePane} />
          <SidePane ref={sidePaneRef}>
            <SidePaneContent>
              <SidePaneItem>Profile</SidePaneItem>
              <SidePaneItem>Upload PDF</SidePaneItem>
              <SidePaneItem>Get Supplies</SidePaneItem>
            </SidePaneContent>
          </SidePane>
        </>
      )}

      <Footer>
        <p>¬© 2023 Kitaab Ghar - Your Education Video Course Website</p>
      </Footer>
    </AppContainer>
  );
}

export default App;
